import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';

export default function LoginPage() {

  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  const submit = () => {
    let usernameCorrect, passwordCorrect
    fetch("http://localhost:5000/login/login", {
      headers: {
        "Content-Type": "application/json"
      },
      method: 'POST',
      body: JSON.stringify({
        username: username,
        password: password
      }),
    })
    .then(response => response.json())
    .then((username) => { if(username != this.username) {
          usernameCorrect = false
    } else {
      usernameCorrect = true
    }
  })
    .then((password) => { if(password != this.password) {
          passwordCorrect = false
    } else {
      passwordCorrect = true
    }
  })
     alert('Response sent !')
    .catch(error => console.log('error', error));
  }

  return (
  <div>
    <center>
   <div>
    <TextField
              onChange={(e) => setUsername(e.target.value)}
              required
              id="outlined-required"
              label="Username"
              variant="outlined"
            />
    </div>
    <div>
    <TextField
              onChange={(e) => setPassword(e.target.value)}
              required
              id="outlined-password-input"
              label="Password"
              type="password"
              autoComplete="current-password"
              variant="outlined"
    />
    </div>
    <div>
    <Button onClick={submit}><Link to= '/homepage'>Login</Link></Button>
    </div>
    <div>
    <Button><Link to= '/signup'>Sign up</Link></Button>
    </div>
    </center>
  </div>
  )
}
