import React from 'react'
import {
  Link,
  useParams
} from "react-router-dom";
import Thread from '../components/Thread.js'
import { makeStyles } from '@material-ui/core/styles';
import questionButton from '../Pages/questionButton.module.css'
import ScrollTop from '../components/ScrollTop.js'


const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    height: 400,
    maxWidth: 600,
    backgroundColor: theme.palette.background.paper,
  },
}));

const threads = [
  {
    title: 'How are black holes created?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'Is Schrodinger cat dead or alive?',
    subject:'Physics',
    author: 'Jadeups'
  },
  {
    title: 'What is a boson?',
    subject:'Physics',
    author: 'Jadeups'
  },
  {
    title: 'How did they calculate the speed of light?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'Was Einstein really against quantum physics?',
    subject:'Physics',
    author: 'Sandra'
  },
  {
    title: 'Did Richard Feynman understand all quantum physics?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'How smart is Neil De Grasse Tyson compared to Einstein?',
    subject:'Physics',
    author: 'Steven'
  },
  {
    title: 'Is Stephen Hawking really a genius?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'How do supernovas form?',
    subject:'Physics',
    author: 'Joseph'
  },
  {
    title: 'Which is lighter, an electron or a proton?',
    subject:'Physics',
    author: 'Fadi'
  },
  {
    title: 'What is gravity?',
    subject:'Physics',
    author: 'Marie'
  },
  {
    title: 'How heavy is a boson?',
    subject:'Physics',
    author: 'Paul'
  },
  {
    title: 'How many quarks exist in 2020?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'How many black holes exist in our galaxy?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'What constitutes matter?',
    subject:'Physics',
    author: 'Ryan'
  },
  {
    title: 'Was Einstein popular before his Nobel Prize?',
    subject:'Physics',
    author: 'Ryan'
  },
  {
    title: 'Do quantitative physcis exist?',
    subject:'Physics',
    author: 'Sarah'
  },
  {
    title: 'How old is the oldest black hole?',
    subject:'Physics',
    author: 'Karl'
  },
  {
    title: 'Do we need black holes to exist?',
    subject:'Physics',
    author: 'Jean'
  },
  {
    title: 'Where will be earth in the universe in a million years?',
    subject:'Physics',
    author: 'Marc'
  },
  {
    title: 'What would happen if the sun exploded now?',
    subject:'Physics',
    author: 'Jadeups'
  },
  {
    title: 'Is relativity verified?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'How are mitochondrias created?',
    subject:'Biology',
    author: 'Davidov'
  },
  {
    title: 'How are double arrays created?',
    subject:'Computer Science',
    author: 'Sar'
  },
]

function SubjectPage() {
 let { subject } = useParams();

  return (
   // <div className={classes.root}>
   <div>
     <div>
       <input className={questionButton.["c-checkbox"]} type="checkbox" id="checkbox"/>
          <div className={questionButton.["c-formContainer"]}>
            <form className={questionButton.["c-form"]} action="">
              <input className={questionButton.["c-form__input"]} placeholder="Question" type="text"/>
              <label className={questionButton.["c-form__buttonLabel"]} for="checkbox">
                <button className={questionButton.["c-form__button"]} type="button">Ask</button>
              </label>
              <label className={questionButton.["c-form__toggle"]} for="checkbox" data-title="Ask a question"></label>
            </form>
          </div>
     </div>
     <ScrollTop/>
     <h1>
          {threads
     .filter((thread) => {
       return thread.subject === subject
 })
    .map((thread) => (
      <Link to={{pathname: '/thread/' + thread.title}} ><Thread
      title={thread.title}/>
      </Link>
    ))}
    </h1>
   </div>
  );   
}
export default SubjectPage
