import React, {Component, useState} from 'react'
import {
  Link,
  useParams
} from "react-router-dom";
import PropTypes from 'prop-types'
import Thread from '../components/Thread.js'
import { makeStyles } from '@material-ui/core/styles';
import CreateQuestion from '../components/CreateQuestion.js'
import questionButton from '../Pages/questionButton.module.css'
import {FaArrowCircleUp} from 'react-icons/fa';


const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    height: 400,
    maxWidth: 600,
    backgroundColor: theme.palette.background.paper,
  },
}));

const threads = [
  {
    title: 'How are black holes created?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'How are mitochondrias created?',
    subject:'Biology',
    author: 'Davidov'
  },
  {
    title: 'How are double arrays created?',
    subject:'Computer Science',
    author: 'Sar'
  },
]

function SubjectPage() {
  const [showScroll, setShowScroll] = useState(false)
  const checkScrollTop = () => {    
     if (showScroll && window.pageYOffset > 400){
        setShowScroll(false)    
     } else if (showScroll && window.pageYOffset <= 400){
        setShowScroll(true)    
     }  
  };
  window.addEventListener('scroll', checkScrollTop)

  const scrollTop = () =>{
    window.scrollTo({top: 0, behavior: 'smooth'});
 };
 let { subject } = useParams();

  return (
   // <div className={classes.root}>
   <div>
     <div>
       <input className={questionButton.["c-checkbox"]} type="checkbox" id="checkbox"/>
          <div className={questionButton.["c-formContainer"]}>
            <form className={questionButton.["c-form"]} action="">
              <input className={questionButton.["c-form__input"]} placeholder="Question" type="text"/>
              <label className={questionButton.["c-form__buttonLabel"]} for="checkbox">
                <button className={questionButton.["c-form__button"]} type="button">Ask</button>
              </label>
              <label className={questionButton.["c-form__toggle"]} for="checkbox" data-title="Ask a question"></label>
            </form>
          </div>
     </div>
     <h1>
       <div>
        <FaArrowCircleUp 
          className="scrollTop" 
          onClick={scrollTop} 
          style={{height: 40, display: showScroll ? 'flex' : 'none'}}
        />
       </div>
          {threads
     .filter((thread) => {
       return thread.subject === subject
 })
    .map((thread) => (
      <Link to={{pathname: '/thread/' + thread.title}} ><Thread
      title={thread.title}/>
      </Link>
    ))}
    </h1>
   </div>
  );   
}
SubjectPage.propTypes = {
  data: PropTypes.string.isRequired
}
export default SubjectPage
