import React, {Component} from 'react'
import {
  Link,
  useParams
} from "react-router-dom";
import PropTypes from 'prop-types'
import Thread from '../components/Thread.js'
import { makeStyles } from '@material-ui/core/styles';
import CreateQuestion from './components.CreateQuestion.js'


const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    height: 400,
    maxWidth: 600,
    backgroundColor: theme.palette.background.paper,
  },
}));

const threads = [
  {
    title: 'How are black holes created?',
    subject:'Physics',
    author: 'Rayyoushi'
  },
  {
    title: 'How are mitochondrias created?',
    subject:'Biology',
    author: 'Davidov'
  },
  {
    title: 'How are double arrays created?',
    subject:'Computer Science',
    author: 'Sar'
  },
]

function SubjectPage() {
 let { subject } = useParams();

  return (
   // <div className={classes.root}>
   <div>
     <div>
       <CreateQuestion/>
     </div>
          {threads
     .filter((thread) => {
       return thread.subject === subject
 })
    .map((thread) => (
      <Link to={{pathname: '/thread/' + thread.title}} ><Thread
      title={thread.title}/>
      </Link>
    ))}
   </div>
  );   
}
SubjectPage.propTypes = {
  data: PropTypes.string.isRequired
}
export default SubjectPage
