import React from 'react';
import {Helmet} from 'react-helmet'
import {Link, BrowserRouter, Switch as SwitchReact, Route} from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import ButtonBase from '@material-ui/core/ButtonBase';
import Typography from '@material-ui/core/Typography';
import physicsImg from './Images/Physics.jpg'
import biologyImg from './Images/Biology.jpg'
import csImg from './Images/Computer_Science.jpg'
import { Switch } from '@material-ui/core';
import SubjectPage from './Pages/SubjectPage'
import HomePage from './Pages/HomePage'

const images = [
  {
    url: physicsImg,
    title: 'Physics',
    width: '40%',
  },
  {
    url: biologyImg,
    title: 'Biology',
    width: '30%',
  },
  {
    url: csImg,
    title: 'Computer Science',
    width: '30%',
  },
];

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    minWidth: 300,
    width: '100%',
  },
  image: {
    position: 'relative',
    height: 200,
    [theme.breakpoints.down('xs')]: {
      width: '100% !important', // Overrides inline-style
      height: 100,
    },
    '&:hover, &$focusVisible': {
      zIndex: 1,
      '& $imageBackdrop': {
        opacity: 0.15,
      },
      '& $imageMarked': {
        opacity: 0,
      },
      '& $imageTitle': {
        border: '4px solid currentColor',
      },
    },
  },
  focusVisible: {},
  imageButton: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: theme.palette.common.white,
  },
  imageSrc: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    backgroundSize: 'cover',
    backgroundPosition: 'center 40%',
  },
  imageBackdrop: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    backgroundColor: theme.palette.common.black,
    opacity: 0.4,
    transition: theme.transitions.create('opacity'),
  },
  imageTitle: {
    position: 'relative',
    padding: `${theme.spacing(2)}px ${theme.spacing(4)}px ${theme.spacing(1) + 6}px`,
  },
  imageMarked: {
    height: 3,
    width: 18,
    backgroundColor: theme.palette.common.white,
    position: 'absolute',
    bottom: -2,
    left: 'calc(50% - 9px)',
    transition: theme.transitions.create('opacity'),
  },
}));

export default function ButtonBases() {
  const classes = useStyles();

  return (
    <BrowserRouter>
      <SwitchReact>
        <Route path= '/subject'>
         <SubjectPage/>
        </Route>
        <Route path='/'>
          <HomePage/>
        </Route>
      </SwitchReact>
    </BrowserRouter>
  );
}
