import React, {useEffect, useRef, useState} from 'react';
import {useParams} from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import CreateAnswer from '../components/CreateAnswer'
import ScrollTop from '../components/ScrollTop.js'
import Axios from 'axios'
import Modal from '@material-ui/core/Modal';
import buttonStyle from '../components/button.module.css'

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '200ch',
    backgroundColor: theme.palette.background.paper,
  },
  inline: {
    display: 'inline',
  },
}));

const useStylesModal = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },

}));
function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `50%`,
    left: `50%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}



export default function AlignItemsList() {
  const classes = useStyles();
  const classesModal = useStylesModal()
  let {thread} = useParams()
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);
  const inputAnswer = useRef(null);
  console.log(inputAnswer)
  const submitAnswer = () => {
    Axios({
      method: "POST",
      data: {
         username: 'testUsername',
         subject: 'testSubject',
         body: inputAnswer,
      },
      withCredentials: true,
      url: "http://localhost:5000/threads/answer",
      timeout: 4000
    }).then((res) => {
      console.log(res)
      setOpen(false)
    })
      .catch(console.log('Error posting answer'))
  }

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div style={modalStyle} className={classesModal.paper}>
      <h2 id="simple-modal-title">Write your answer!</h2>
      <div>
      <textarea ref={inputAnswer} rows="10" cols="40" placeholder="Write answer here"></textarea></div>
      <button type="submit" onClick={handleClose}>Cancel</button>
      <button type="submit" onClick={submitAnswer}>Ok</button>
    </div>
  );

  const [messages, setMessages]= useState([
    {
      title: 'Supernova',
      username: 'Jadeups',
      text: 'Interesting question! I think it has to do with voliminous dying stars.'
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: 'Indeed, but how, more specifically ?'
    },
    {
      title: 'How are black holes created?',
      username: 'Jadeups',
      text: "I don't know I am looking for the answer at the moment"
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: 'They form when stars that are voluminous enough die'
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "I agree, but isn't there any other way to form black holes?"
    },
    {
      title: 'How are black holes created?',
      username: 'Sar',
      text: "Not all stars form black holes"
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: "Yes we know, we said that they have to be volumious enough"
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "Exactly"
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "Doesn't this conversation spark the interest of more users? hahaha"
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: "This forum is quite new, so there aren't many members."
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "Yeah we're like 100 people on the forum tops I think."
    },
    {
      title: 'How are black holes created?',
      username: 'Sar',
      text: "I subscribed two months ago."
    },
    {
      title: 'How are mitochondrias created?',
      username: 'Davidov',
      text: 'No idea haha, not interesting'
    },
    {
      title: 'How are mitochondrias created?',
      username: 'Sar',
      text: 'Stop spamming Davidov'
    }
  ])
  useEffect(() => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:5000/threads/answers",
      timeout: 4000
    }).then((res) => {
      console.log(res)
      setMessages(res)
    })
  })

  return (
    <div>

    <button type="button" className={buttonStyle.coolBeans}onClick={handleOpen}>
        Write your answer
      </button>
      <Modal
        open={open}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    <ScrollTop/>
    <List className={classes.root}>

    {messages
     .filter((message) => {
      return message.title === thread
     })
    .map((message) => (
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
        </ListItemAvatar>
        <ListItemText
          primary={thread}
          secondary={
            <React.Fragment>
              <div>
              <Typography
                component="span"
                variant="body2"
                className={classes.inline}
                color="textPrimary"
              >
                {message.username}
              </Typography>
              </div>
               {message.text}
            </React.Fragment>
          }
        />
      </ListItem>
    ))}
    </List>
    </div>
  );
}
