import React from 'react';
import {useParams} from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import CreateAnswer from '../components/CreateAnswer'
import ScrollTop from '../components/ScrollTop.js'

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '200ch',
    backgroundColor: theme.palette.background.paper,
  },
  inline: {
    display: 'inline',
  },
}));

export default function AlignItemsList() {
  const classes = useStyles();
  let {thread} = useParams()

  const messages= [
    {
      title: 'How are black holes created?',
      username: 'Jadeups',
      text: 'Interesting question! I think it has to do with voliminous dying stars.'
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: 'Indeed, but how, more specifically ?'
    },
    {
      title: 'How are black holes created?',
      username: 'Jadeups',
      text: "I don't know I am looking for the answer at the moment"
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: 'They form when stars that are voluminous enough die'
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "I agree, but isn't there any other way to form black holes?"
    },
    {
      title: 'How are black holes created?',
      username: 'Sar',
      text: "Not all stars form black holes"
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: "Yes we know, we said that they have to be volumious enough"
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "Exactly"
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "Doesn't this conversation spark the interest of more users? hahaha"
    },
    {
      title: 'How are black holes created?',
      username: 'Rayyouch',
      text: "This forum is quite new, so there aren't many members."
    },
    {
      title: 'How are black holes created?',
      username: 'Davidov',
      text: "Yeah we're like 100 people on the forum tops I think."
    },
    {
      title: 'How are black holes created?',
      username: 'Sar',
      text: "I subscribed two months ago."
    },
    {
      title: 'How are mitochondrias created?',
      username: 'Davidov',
      text: 'No idea haha, not interesting'
    },
    {
      title: 'How are mitochondrias created?',
      username: 'Sar',
      text: 'Stop spamming Davidov'
    }
  ]
  return (
    <div>
    <CreateAnswer/>
    <ScrollTop/>
    <List className={classes.root}>

    {messages
     .filter((message) => {
      return message.title === thread
     })
    .map((message) => (
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
        </ListItemAvatar>
        <ListItemText
          primary={thread}
          secondary={
            <React.Fragment>
              <div>
              <Typography
                component="span"
                variant="body2"
                className={classes.inline}
                color="textPrimary"
              >
                {message.username}
              </Typography>
              </div>
               {message.text}
            </React.Fragment>
          }
        />
      </ListItem>
    ))}
    </List>
    </div>
  );
}
